import React, { useEffect, useState } from "react";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEllipsis } from "@fortawesome/free-solid-svg-icons";

export default function TransactionList() {
  
    const [transactionList, setTransactionList] = useState([])

    useEffect(() => {
        axios.get("http://localhost:3001/transaction/list", {
            headers: {
                token: localStorage.getItem("token")
            }
        })
        .then((response) =>{
            setTransactionList(response.data)
        })
    }, [])


    const style = {

     height: transactionList.length === 0 ? '110vh' : ''

    }

  return (
    <div className="transaction--list" style={style}>
      {transactionList.map((value, key) => {
        const type = (value.type).toLowerCase()
        return (
          <div className="transactions--cards">
            <div className="transactions--card">
              <h2 className="concept">{value.concept} </h2>
              <h3 className="date">{value.date}</h3>
              <h6 className="type">Your {type} was: <span>${value.amount}</span></h6>
              <p className="comment">{value.comment}</p>
              <h6></h6>
            </div>
          </div>
        );
      })}

      {
        transactionList.length === 0 && <p className="nothing">There is nothing here</p> 
      }

    </div>
  );
}
