import React, { useState } from "react";
import axios from "axios";

export default function Transaction() {
  const [concept, setConcept] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(Date.now());
  const [type, setType] = useState("");
  const [comment, setComment] = useState("");

  const addTransaction = () => {};

  return (
    <div>
      <label>Title</label>
      <input type="text" name="concept" placeholder="July's Salary" onChange={((event) => {setConcept(event.target.value)})}></input>
      <label>Amount</label>
      <input type="number" name="amount" placeholder="500,50"></input>
      <label>Date</label>
      <input type="date"></input>
      <label>Type</label>
      <select value={type}>
        <option value="">---select type of transaction</option>
        <option value="Income">Income</option>
        <option value="Egress">Egress</option>
      </select>
      <label>Extra comment</label>
      <textarea
        class="form-control"
        name="comment"
        rows="5"
        cols="60"
        placeholder="Add any aditional comments"
      ></textarea>
      <button type="submit">Submit</button>
    </div>
  );
}
