import React, { useState } from "react";
import axios from "axios";

export default function Transaction() {
  const [concept, setConcept] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(Date.now());
  const [type, setType] = useState("");
  const [comment, setComment] = useState("");

  const [transactions, setTransactions] = useState([]);

  const addTransaction = () => {
    axios
      .post(
        "http://localhost:3001/transaction",
        {
          headers: {
            token: localStorage.getItem("item"),
          },
        },
        {
          concept: concept,
          amount: amount,
          date: date.toString(),
          type: type,
          comment: comment,
        }
      )
      .then(() => {
        setTransactions([
          ...transactions,
          {
            concept: concept,
            amount: amount,
            date: date.toString(),
            type: type,
            comment: comment,
          },
        ]);
      });
  };

  return (
    <div>
      <label>Title</label>
      <input
        type="text"
        name="concept"
        placeholder="July's Salary"
        onChange={(event) => {
          setConcept(event.target.value);
        }}
      ></input>
      <label>Amount</label>
      <input
        type="number"
        name="amount"
        placeholder="500,50"
        onChange={(event) => {
          setAmount(event.target.value);
        }}
      ></input>
      <label>Date</label>
      <input
        type="date"
        name="date"
        onChange={(event) => {
          setDate(event.target.value);
        }}
      ></input>
      <label>Type</label>
      <select
        value={type}
        name="type"
        onChange={(event) => {
          setType(event.target.value);
        }}
      >
        <option value="">---select type of transaction</option>
        <option value="Income">Income</option>
        <option value="Egress">Egress</option>
      </select>
      <label>Extra comment</label>
      <textarea
        name="comment"
        rows="5"
        cols="60"
        placeholder="Add any aditional comments"
        onChange={(event) => {
          setComment(event.target.value);
        }}
      ></textarea>
      <button type="submit" onClick={addTransaction}>
        Submit
      </button>
    </div>
  );
}
