import React, { useEffect, useState } from "react";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEllipsis } from "@fortawesome/free-solid-svg-icons";
import { useHistory } from "react-router-dom";
import { faTrashCan } from "@fortawesome/free-solid-svg-icons";
import { useParams } from "react-router-dom";

export default function TransactionList() {
  const [transactionList, setTransactionList] = useState([]);
  let history = useHistory();
  let { id } = useParams();

  useEffect(() => {
    axios
      .get("http://localhost:3001/transaction/list", {
        headers: {
          token: localStorage.getItem("token"),
        },
      })
      .then((response) => {
        setTransactionList(response.data);
      });
  }, []);

  const style = {
    height: transactionList.length === 0 ? "110vh" : "",
  };

  const deleteTransaction = (id) => {
    axios.delete(`http://localhost:3001/transaction/list/${id}`)
    .then(() => {
      setTransactionList(transactionList.filter((val) => {
        return val.id !== id;
      }))
    })
  }

  function useForceUpdate(){
    const [value, setValue] = useState(0); // integer state
    return () => setValue(value => value + 1); // update the state to force render
  }
    const forceUpdate = useForceUpdate();
  
  return (
    <div className="transaction--list" style={style}>
      {transactionList.map((value, key) => {
        const type = value.type.toLowerCase();
        return (
          <div className="transactions--cards">
            <div className="transactions--card">
              <div className="icon--trash">
                <FontAwesomeIcon 
                icon={faTrashCan}
                onClick={() => {deleteTransaction(value.id);}}
                >
                </FontAwesomeIcon>
              </div>

              <div className="icon--readmore">
                <FontAwesomeIcon
                  icon={faEllipsis}
                  onClick={() => {
                    history.push(`/transaction/${value.id}`);
                  }}
                ></FontAwesomeIcon>
              </div>

              <h2 className="concept">{value.concept} </h2>
              <h3 className="date">{value.date}</h3>
              <h6 className="type">
                Your {type} was: <span>${value.amount}</span>
              </h6>
              <p className="comment">{value.comment}</p>
            </div>
          </div>
        );
      })}

      {transactionList.length === 0 && (
        <p className="nothing">There is nothing here</p>
      )}
    </div>
  );
}
